import { Component, OnInit, OnDestroy } from '@angular/core';
import * as socketIo from 'socket.io-client';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent implements OnInit, OnDestroy {
  private socket: any;
  public board: string[] = Array(9).fill(null);
  public player: string = '';
  public currentTurn: string = 'X';
  public gameId: string = 'game1';
  status: string = '';
  isGameOver: boolean = false;

  constructor() {
    this.socket = socketIo.connect('http://localhost:3000');
    this.player = Math.random() < 0.5 ? 'X' : 'O';
  }

  ngOnInit(): void {
    this.socket.emit('joinGame', { gameId: this.gameId, player: this.player });
    this.updateStatus();

    this.socket.on('gameState', (gameState: any) => {
      this.board = gameState.board;
      this.currentTurn = gameState.currentTurn;
      this.updateStatus();
    });

    this.socket.on('gameOver', (data: any) => {
      this.isGameOver = true;
      if (data.winner) {
        this.status = data.winner === this.player ? 'You won!' : 'You lost!';
      } else {
        this.status = "It's a draw!";
      }

      // Reset after 2 seconds
      setTimeout(() => {
        this.isGameOver = false;
        this.board = Array(9).fill(null);
        this.currentTurn = 'X';
        this.updateStatus();
      }, 2000);
    });
  }

  updateStatus(): void {
    if (!this.isGameOver) {
      this.status = this.currentTurn === this.player ? 
        'Your turn!' : 
        "Opponent's turn!";
    }
  }

  makeMove(index: number): void {
    if (this.currentTurn === this.player && this.board[index] === null && !this.isGameOver) {
      this.socket.emit('makeMove', {
        gameId: this.gameId,
        index: index,
        player: this.player
      });
    }
  }

  ngOnDestroy(): void {
    if (this.socket) {
      this.socket.disconnect();
    }
  }
}
